using System; using System.Collections.Generic; using System.Linq; using System.Text; using System.Threading.Tasks;  namespace SeguridadNacional {     internal class Repositorio     {         internal static string[] opciones = {"\n 1 Crear un mensaje" ,                     "\n 2 Cifrar mensaje utilizando el algoritmo AES" ,                     "\n 3 Cifrar mensaje utilizando el algoritmo Cifrado Matricula" ,                     "\n 4 Cifrar mensaje utilizando el algoritmo Cifrado Nombre" ,                     "\n 5 Destruir el mensaje" };          static string? mensaje = null;          // Instancias de las clases usadas         static Cifrado mcifrado;         static CifradoMatricula cMatricula;         static CifradoNombre cNombre;           public static void escribirMensaje()         {             Console.WriteLine("Escriba su mensaje");             mensaje = Console.ReadLine();         }          public static void pregunta()         {             Console.WriteLine("El mensaje a cifrar es: \"" + mensaje + "\" Desea cambiarlo: Y/N");             string r = Console.ReadLine();             if (r == "Y" || r == "y" )                 escribirMensaje();         }           /** * *******  *******  *******  mensaje  *******  *******  ******* */         public static void ro1()         {             Console.WriteLine(opciones[0] + ": ");             escribirMensaje();         }           /** * *******  *******  *******  AES  *******  *******  ******* */         public static void ro2()          {             Console.WriteLine(opciones[1] + ": ");              pregunta();              // Se instancia un objeto CifradoMatricula, con el tipo padre Cifrado             // el cual recibe el mensaje previamente guardado             mcifrado = new CifradoMatricula("",mensaje);             mcifrado.Cifrar();         }           /** * *******  *******  *******  matrícula  *******  *******  ******* */         public static void ro3()         {             Console.WriteLine(opciones[2] + ": ");              pregunta();             // se instancia objeto para cifrarlo con matricula             cMatricula = new CifradoMatricula("ES1822035905", mensaje);                          cMatricula.Cifrar();             cMatricula.OCifrar();              cMatricula.EnviarMensaje("Email");             cMatricula.EnviarMensaje(123456);         }           /** * *******  *******  *******  nombre  *******  *******  ******* */         public static void ro4()         {             Console.WriteLine(opciones[3] + ": ");              pregunta();             // se instancia objeto para cifrarlo con matricula             cNombre = new CifradoNombre("Alejandro", mensaje);             cNombre.Cifrar();         }           /** * *******  *******  *******  destruir  *******  *******  ******* */         public static void ro5()         {             Console.WriteLine(opciones[4] + ": ");              Console.WriteLine("Usted escribio: " + mensaje );              // borramos el atributo de la clase Repositorio             mensaje = null;             // borramos los mensajes de las instancias             mcifrado.DestruirMensaje();             cMatricula.DestruirMensaje();             cNombre.DestruirMensaje();              Console.WriteLine("\tEl mensaje a sido borrado en todas las instancias ...");         }               } } 