using System; using System.Collections.Generic; using System.Linq; using System.Reflection; using System.Text; using System.Threading.Tasks;  namespace u3_a3_alac {     internal class Repositorio     {         internal static string[] opciones = {                     "\n 1 Generar pesos aleatorios" ,                     "\n 2 Capturar pesos de alumnos" ,                     "\n 3 Capturar un peso de alumno" ,                     "\n 4 Mostrar histograma"         };          static string? mensaje = null;         static HistogramaPeso histograma = null;           public static int generarPesoRandom()         {             var random = new Random(); // crea un objeto Random             int peso = random.Next(40, 120); // genera un número entre 40 y 120             return peso;         }            /** * *******  *******  *******  ___  *******  *******  ******* */         public static void ro1()         {             Console.WriteLine(opciones[0] + ": ");             histograma = new HistogramaPeso();             //histograma.mostrarHistograma();         }           /** * *******  *******  *******  ___  *******  *******  ******* */         public static void ro2()          {             Console.WriteLine(opciones[1] + ": ");             Console.Write("Ingrese el numero de alumnos: ");             int alumnos = Convert.ToInt16(Console.ReadLine());             histograma = new HistogramaPeso(alumnos);             for (int i = 0; i < alumnos; i++)                 histograma.ingresarPeso(i);         }           /** * *******  *******  *******  ___  *******  *******  ******* */         public static void ro3()         {             Console.WriteLine(opciones[2] + ": ");             if (histograma != null)             {                 try                 {                     histograma.ingresarPeso();                 }                 catch (RangoFueraException rfe)                 {                     Console.WriteLine(rfe);                 }                              }             else             {                 Console.WriteLine("Primero seleccione la opción 1 o 2.");             }         }           /** * *******  *******  *******  ___  *******  *******  ******* */         public static void ro4()         {             Console.WriteLine(opciones[3] + ": ");             if (histograma != null)                 histograma.mostrarHistograma();             else                 Console.WriteLine("Primero almacene los pesos de los alumnos.");         }      } } 